package ogs.leetcode.secondcoming;

import java.time.Instant;

public class Palindrome {
    /*
Given a string s, return the longest
palindromic
substring
in s.

Example 1:

Input: s = "babad"
Output: "bab"
Explanation: "aba" is also a valid answer.

Example 2:

Input: s = "cbbd"
Output: "bb"

Constraints:

    1 <= s.length <= 1000
    s consist of only digits and English letters.

*/
    //time exceeded
    
    
    //117
    

    //106
    //ccd

    //126/141 214 ms vkkv 200
    
    public static void main(String[] args) {
        System.out.println(longestPalindrome(
            ""));
    }

    //starting the pointers at the center and moving outwards
    public static String longestPalindrome(String s) {
        //Instant start = Instant.now();

        int[] finalResult = new int[]{0, 0, 0};

        for (int i = 0; i < s.length(); i++) {
            //для четных
            int resOdd[] = find(i, i, s);
            if (finalResult[2] < resOdd[2]) {
                finalResult[2] = resOdd[2];
                finalResult[0] = resOdd[0];
                finalResult[1] = resOdd[1];
            }
            //для нечетных
            int resEven[] = find(i, i + 1, s);
            if (finalResult[2] < resEven[2]) {
                finalResult[2] = resEven[2];
                finalResult[0] = resEven[0];
                finalResult[1] = resEven[1];
            }
        }
        //System.out.println(Instant.now().toEpochMilli() - start.toEpochMilli());

        return s.substring(finalResult[0], finalResult[1]);
    }

    private static int[] find(int i, int j, String s) {
        int left = i, right = j;
        int res = 0;
        int start = 0;
        int end = 0;
        while (left >= 0 && right < s.length() && s.charAt(left) == s.charAt(right)) {
            left--;
            right++;
        }
        int length = (right - left - 1);
        if (res < length) {
            res = length;
            start = left + 1;
            end = right;
        }
        //System.out.println("left: " + left + ", right: " + right + ", length -> " + length + ", palindrome: " + s.substring(start, end));
        return new int[]{start, end, res};
    }

    public static String longestPalindromeOld(String s) {
        Instant start = Instant.now();
        int maxStringLength = s.length();
        for (int i = maxStringLength; i > 0; i--) {
            for (int startPos = 0; startPos <= s.length() - i; startPos++) {
                String substring = s.substring(startPos, startPos + i);
                if (isPal(substring)) {
                    System.out.println(Instant.now().toEpochMilli() - start.toEpochMilli());
                    return substring;
                }
            }
        }
        System.out.println(Instant.now().toEpochMilli() - start.toEpochMilli());
        return "1";
    }

    public static boolean isPal(String s) {
        return s.equals(new StringBuilder(s).reverse().toString());
    }
}
